import * as React from 'react';
import Button, { ButtonProps } from '@material-ui/core/Button';
import {
  withStyles,
  WithStyles,
  Theme,
  StyleRulesCallback
} from '@material-ui/core/styles';
import withRoot from '../withRoot';

const styles: any = (theme: Theme) => ({
  button: {
    height: '48px',
    textTransform: 'none',
    fontSize: '18px',
    fontWeight: 'bold',
    borderRadius: '10px'
  },
  default: {
    color: '#fff',
    backGroundColor: theme.pallete
  }
});

interface AppButtonProps extends ButtonProps {
  component?: React.ReactType<ButtonProps> | React.ComponentType;
  to?: string;
}

interface Props extends WithStyles<typeof styles> {
  component?: React.ComponentType;
  children?: string;
  path: string;
  onClick?: any;
  fullWidth?: boolean;
  buttonType: string;
  to: string;
  type: string;
}

const MyAppButton = (props: AppButtonProps) => <Button {...props} />;

const AppButton: React.FunctionComponent<Props> = props => {
  return (
    <>
      <MyAppButton
        className={props.classes.default}
        variant="contained"
        fullWidth={props.fullWidth}
        component={props.component}
        to={props.path}
        onClick={props.onClick}
        type={props.type}
      >
        {props.children}
      </MyAppButton>
    </>
  );
};

export default withRoot(withStyles(styles)(AppButton));
