import * as React from 'react';
import Button, { ButtonProps } from '@material-ui/core/Button';
// import { RouteComponentProps } from 'react-router-dom';
import {
  withStyles,
  WithStyles,
  Theme,
  StyleRulesCallback
} from '@material-ui/core/styles';
import withRoot from '../withRoot';

const styles: any = (theme: Theme) => ({
  button: {
    height: '48px',
    textTransform: 'none',
    fontSize: '18px',
    fontWeight: 'bold',
    borderRadius: '10px'
  },
  default: {
    color: theme.palette.primary.light
  },
  register: {
    backgroundColor: 'transparent',
    borderColor: theme.palette.primary.light,
    marginTop: '10px',
    color: theme.palette.primary.light,
    border: '2px solid',
    '&:hover': {
      backgroundColor: 'transparent',
      opacity: '0.7'
    }
  }
});

interface AppButtonProps extends ButtonProps {
  component?: React.ReactType<ButtonProps> | React.ComponentType;
  to?: string;
}

interface Props extends WithStyles<typeof styles> {
  component?: React.ComponentType;
  children?: string;
  path: string;
  onClick?: any;
  fullWidth?: boolean;
  buttonType: string;
  to: string;
  type: string;
}

const MyAppButton = (props: AppButtonProps) => <Button {...props} />;

const _renderButtonType = (props: Props, buttonType: string) => {
  return buttonType === 'default'
    ? [props.classes.default, props.classes.button].join(' ')
    : [props.classes.register, props.classes.button].join(' ');
};

const AppButton: React.FunctionComponent<Props> = props => {
  return (
    <>
      <MyAppButton
        className={_renderButtonType(props, props.buttonType)}
        variant="contained"
        color="secondary"
        fullWidth={props.fullWidth}
        component={props.component}
        to={props.path}
        onClick={props.onClick}
        type={props.type}
      >
        {props.children}
      </MyAppButton>
    </>
  );
};

export default withRoot(withStyles(styles)(AppButton));
